import { useCallback } from 'react'
import styled from 'styled-components'

import { colors, setTransition, typos, WalletIcon } from '@klaytn/slush'

import { useConfig } from '../../../context/configProvider'
import { getThemeColor } from '../../../functions/colorMap'
import { useFinderThemeColor } from '../../../hooks/useFinderThemeColor'
import { WalletType } from '../../../states/wallet'

type SelectWalletStepProps = {
    onSelect(walletType: WalletType): void
    onSelectTestWallet(): void
}

export const SelectWalletStep = ({ onSelect, onSelectTestWallet }: SelectWalletStepProps) => {
    const { network, target } = useConfig()
    const iconColor = useFinderThemeColor(colors.white)

    const showTestWallet = target === 'local' || target === 'stage'

    const handleKaikas = useCallback(() => {
        onSelect('KAIKAS')
    }, [onSelect])

    const handleMetamask = useCallback(() => {
        onSelect('METAMASK')
    }, [onSelect])

    const handleKlip = useCallback(() => {
        onSelect('KLIP')
    }, [onSelect])

    return (
        <Container>
            <TopContainer>
                <WalletIcon size={64} color={iconColor} />
                <Title>Please Connect a Wallet</Title>
                <Description>
                    To use this feature,
                    <br />
                    you need to connect wallet.
                </Description>
            </TopContainer>

            <ButtonContainer>
                <KaikasButton onClick={handleKaikas}>
                    <KaikasLogo />
                    Kaikas
                </KaikasButton>
                <MetamaskButton onClick={handleMetamask}>
                    <MetamaskLogo />
                    Metamask
                </MetamaskButton>
                {network === 'cypress' && ( // klipÏùÄ Supports cypress only
                    <KlipButton onClick={handleKlip}>
                        <KlipLogo />
                        Klip
                    </KlipButton>
                )}
                {showTestWallet && <KlipButton onClick={onSelectTestWallet}>Test Wallet</KlipButton>}
            </ButtonContainer>
        </Container>
    )
}

const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 32px;
`

const TopContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 16px;
`

const Title = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    text-align: center;
    color: ${getThemeColor(colors.white)};
    ${typos.suit['18.24_900']};
`

const Description = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    text-align: center;
    color: ${getThemeColor(colors.black[300])};
    ${typos.suit['14.18_400']};
`

const ButtonContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;
    gap: 8px;
`

const KaikasButton = styled.button`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding: 10px 20px;
    gap: 8px;
    background: #3366ff;
    border-radius: 14px;
    border: none;
    outline: none;
    cursor: pointer;
    color: ${colors.white};
    width: 100%;
    ${typos.suit['14.18_400']};
    ${setTransition('opacity')};

    &:hover {
        opacity: 0.8;
    }

    &:active {
        opacity: 1;
    }
`

const MetamaskButton = styled(KaikasButton)`
    background: #000000;
`

const KlipButton = styled(KaikasButton)`
    background: #2d77ff;
`

